mapper.select:
SELECT id,name,title,created,modified,content,author_id as authorId ,hits,tags,category,status,type,allow_comment as allowComment ,comment_count as commentCount FROM posts WHERE 1=1
AND type=#{postType}
AND status=#{postStatus}
ORDER BY hits DESC,modified,created DESC DESC
ORDER BY modified DESC,created DESC

mapper.insert:
INSERT INTO posts(
    created
    ,modified
    ,type
    ,author_id

        ,title


        ,content


        ,tags


        ,category


        ,status


        ,allow_comment

)
VALUES(
    current_timestamp
    ,current_timestamp
    ,#{type}
    ,#{authorId}

        ,#{title}


        ,#{content}


         ,#{tags}


        ,#{category}


        ,#{status}


        ,#{allowComment}

)

mapper.update:
UPDATE posts SET
hits = #{hits},
modified=current_timestamp WHERE id=#{postId}
